public class Main {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Hello World");

		// range[ true & false]
		boolean flag;
		flag = true;
		System.out.println(flag);

		// range [-128....+127]
		byte cal;
		cal = 126;
		System.out.println(cal);

		// range [-32768 ......+32767]
		short year;
		year = 2022;
		System.out.println(year);

		// int use 32 bits. range [-2,147,483,648..... +2,147,483,647]

		int date;
		date = 12102021;
		System.out.println(date);

		// long use 64 bits .range
		// [-9,223,372,036,854,775,808.....+9,223,372,036,854,775,807]

		long month;
		month = 4;
		System.out.println(month);

		// float use 32 bits and double 64 bits

		float price;
		price = 145.125f;
		System.out.println(price);

		double weight;
		weight = 25.14d;
		System.out.println(weight);

		// char use 16 bits.

		char ch = '8';
		System.out.println(ch);

	}

}
